import { Component, Input } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-category-view',
  templateUrl: './category-view.component.html',
  styleUrls: ['./category-view.component.scss']
})
export class CategoryViewComponent {
  protected category: String | null = '';

  @Input() images: any = [
    { url: './assets/IMG_6970.jpeg', id: 1 },
    { url: './assets/IMG_6976.jpeg', id: 2 },
    { url: './assets/397938_10151112237470058_492932457_n.jpg', id: 3 },
    { url: './assets/53592_10152223493640058_165711038_o.jpg', id: 4 },
    { url: './assets/262702_10150680721425058_7322063_n.jpg', id: 5 },
    { url: './assets/339782_10151133670150058_1940421845_o.jpg', id: 6 },
    { url: './assets/394721_10151112234690058_1895319742_n.jpg', id: 7 },
    {
      url: './assets/29101657_10160094666705058_3170800344036802560_n.jpg',
      id: 8
    },
    { url: './assets/1052235_10152982851815058_1855764683_o.jpg', id: 9 }
  ];

  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    this.category = this.route.snapshot.paramMap.get('id');
    this.test();
  }

  test(): void {
    console.log('Im this', this);
  }

  navigateToImageDetail(imageDetails: number) {
    console.log(imageDetails);
  }
}
<div class="container">
  <div class="image-container" *ngFor="let image of images; index as i">
    <img
      class="image-item"
      src="{{ image.url }}"
      (click)="navigateToImageDetail(image.id)"
    />
  </div>
</div>
