import { Component, OnInit, Input } from '@angular/core';
import { Categories } from 'src/app/modules/navigation/interfaces/categories';
import { RoutingService } from 'src/app/services/routing/routing.service';

@Component({
  selector: 'app-side-nav',
  templateUrl: './side-nav.component.html',
  styleUrls: ['./side-nav.component.scss']
})
export class SideNavComponent implements OnInit {
  @Input()
  categoriesArray!: Array<Categories>;

  constructor(private routingService: RoutingService) {}

  showMenu: boolean = false;
  menuStyles = {
    width: '3vh',
    height: '0.5vh',
    backgroundColor: 'black',
    margin: '6px 6px'
  };

  ngOnInit(): void {}

  toggleMenu() {
    this.showMenu = !this.showMenu;
  }

  handleCategoryNavigation(categoryCode: String): void {
    this.routingService.navigateToPath(`categories/${categoryCode}`);
  }
}
<div class="app-side-nav-container">
  <div (click)="toggleMenu()" *ngIf="!showMenu" class="menu-icon">
    <app-hamburger-menu-icon
      [menuStyles]="menuStyles"
    ></app-hamburger-menu-icon>
  </div>
  <div *ngIf="showMenu" class="category-list">
    <div class="close-icon" (click)="toggleMenu()">x</div>
    <div *ngFor="let category of categoriesArray" class="category-item">
      <span
        (click)="handleCategoryNavigation(category.code)"
        class="category-title"
        >{{ category.title }}</span
      >
    </div>
  </div>
</div>
